'uses strict';

// addressBook object. Parse string to object, provide methods to pull info

// external libraries
const _ = require('lodash');
const {NodeAddressBook} = require('@hashgraph/sdk/lib/generated/BasicTypes_pb');
const {Certificate} = require('crypto');
const NodeRSA = require('node-rsa');

class addressBook {
  constructor(buffer) {
    let addressBook = NodeAddressBook.deserializeBinary(buffer);
    console.log(`${addressBook.getNodeaddressList().length} node(s) found in address book`);
    this.nodeList = addressBook.getNodeaddressList();
    // this.nodeIdPublicKeyPairs = this.getNodeIdPublicKeyPairs();
    this.nodeIdPublicKeyPairs = {};
    this.setNodeIdPublicKeyPairs();
  }

  getNodeIdPublicKeyPairs() {
    return _.map(this.nodeList, (nodeAddress) => {
      // console.log(`Public key of node ${nodeAddress.getNodeid()} retrieved: RSAPubKey: ${nodeAddress.getRsaPubkey()}`);
      return {nodeId: nodeAddress.getNodeid(), publicKey: this.getNodeRSAPublicKey(nodeAddress.getRsaPubkey())};
    });
  }

  setNodeIdPublicKeyPairs() {
    let nodeCount = 3; // hack remove
    _.forEach(this.nodeList, (nodeAddress) => {
      let node;
      if (_.isUndefined(nodeAddress.getNodeid()) || nodeAddress.getNodeid() == 0) {
        node = Buffer.from(nodeAddress.getMemo()).toString('utf-8');
      } else {
        node = nodeAddress.getNodeid();
      }
      // const node = `0.0.${nodeCount++}`;
      this.nodeIdPublicKeyPairs[node] = {publicKey: nodeAddress.getRsaPubkey()};
      // this.nodeIdPublicKeyPairs[node] = {publicKey: nodeAddress.getRsaPubkey()};
      // console.log(`** setNodeIdPublicKeyPairs, nodeIdPublicKeyPairs[${node}]: ${JSON.stringify(this.nodeIdPublicKeyPairs[node])}`);
    });
  }

  getNodeId() {
    if (_.isUndefined(nodeAddress.getNodeid()) || nodeAddress.getNodeid() == 0) {
      return node;
    }
  }

  getNodeIds() {
    return _.map(this.nodeList, (nodeAddress) => {
      return nodeAddress.getMemo();
    });
  }

  getPublicKey(hexEncodedString) {
    var spkac = {
      '1024': {
        md4:
          '',
        md5:
          '',
        sha1:
          '',
        sha224:
          '',
        sha256:
          '',
        sha384:
          '',
        sha512:
          '',
        rmd160:
          '',
      },
      '2048': {
        md4:
          '',
        md5:
          '',
        sha1:
          '',
        sha224:
          '',
        sha256:
          '',
        sha384:
          '',
        sha512:
          '',
        rmd160:
          '',
      },
      '4096': {
        md4:
          '',
        md5:
          '',
        sha1:
          '',
        sha224:
          '',
        sha256:
          '',
        sha384:
          '',
        sha512:
          '',
        rmd160:
          '',
      },
    };

    const buf = Buffer.from(hexEncodedString, 'hex');
    console.log(`Public key of hexEncodedString '${hexEncodedString}' converted to base64 '${buf.toString('base64')}'`);
    const publicKey = new Certificate().exportPublicKey(Buffer.from(buf.toString('base64')));
    console.log(
      `${
        Certificate.verifySpkac(Buffer.from(buf.toString('base64'))) ? '' : 'In'
      }Valid Public key of hexEncodedString '${hexEncodedString}' converted to '${publicKey.toString('utf-8')}'`
    );
    console.log(publicKey);
  }

  getNodeRSAPublicKey(hexEncodedString) {
    let key = new NodeRSA();
    key.importKey(
      {
        n: Buffer.from(hexEncodedString, 'hex'),
        e: 65537, // public exponent. 65537 by default.
      },
      'components-public'
    );

    // key.verify(buffer, signature, [source_encoding], [signature_encoding]);
    // buffer — {buffer} — data for check, same as encrypt method.
    //   signature — {string} — signature for check, result of sign method.
    //   source_encoding — {string} — same as for encrypt method.
    //   signature_encoding — {string} — encoding of given signature. May be 'buffer', 'binary', 'hex' or 'base64'. Default 'buffer'.
    // return key.exportKey('components-public')
    return key;
  }
}

module.exports = {
  addressBook,
};
